import React, { useEffect, useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import "./FacultyDashboard.css";

const FacultyDashboard = () => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);
  const [faculty, setFaculty] = useState(null);
  const [showEmailForm, setShowEmailForm] = useState(false);
  const [emailData, setEmailData] = useState({
    subject: '',
    message: ''
  });

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/login", { replace: true });
      return;
    }

    fetch("http://localhost:3000/api/faculty/profile", {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => res.json())
      .then((data) => {
        if (data.error) {
          navigate("/login", { replace: true });
        } else {
          setFaculty(data);
          setLoading(false);
        }
      })
      .catch(() => navigate("/login", { replace: true }));
  }, [navigate]);

  const handleEmailSubmit = (e) => {
    e.preventDefault();
    const token = localStorage.getItem("token");
    
    fetch("http://localhost:3000/api/faculty/email-hod", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify(emailData),
    })
      .then(res => res.json())
      .then(data => {
        if (data.success) {
          alert("Email sent successfully!");
        } else {
          alert("Failed to send email. Please try again.");
        }
        setShowEmailForm(false);
        setEmailData({ subject: '', message: '' });
      })
      .catch(() => {
        alert("Failed to send email. Please try again.");
      });
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login", { replace: true });
  };

  if (loading) return (
    <div className="loading-screen">
      <div className="loader"></div>
      <p>Loading Faculty Dashboard...</p>
    </div>
  );

  return (
    <div className="faculty-dashboard">
      <div className="dashboard-header">
        <h2>Faculty Dashboard</h2>
        <button className="logout-btn" onClick={handleLogout}>Logout</button>
      </div>

      <div className="dashboard-cards">
        <Link to="/faculty/students" className="dashboard-card">
          <h3>View Students</h3>
          <p>Manage and view your assigned students</p>
        </Link>

        <Link to="/faculty/remarks" className="dashboard-card">
          <h3>Student Remarks</h3>
          <p>Add and manage student remarks</p>
        </Link>

        <Link to="/faculty/profile" className="dashboard-card">
          <h3>Your Profile</h3>
          <p>View and edit your profile</p>
        </Link>

        <div className="dashboard-card" onClick={() => setShowEmailForm(true)}>
          <h3>Contact HOD</h3>
          <p>Send email to department head</p>
        </div>
      </div>

      {showEmailForm && (
        <div className="email-modal">
          <div className="email-modal-content">
            <h3>Email to HOD</h3>
            <form onSubmit={handleEmailSubmit}>
              <div className="form-group">
                <label>Subject</label>
                <input 
                  type="text"
                  value={emailData.subject}
                  onChange={(e) => setEmailData({...emailData, subject: e.target.value})}
                  required
                  placeholder="Enter email subject"
                />
              </div>
              <div className="form-group">
                <label>Message</label>
                <textarea
                  value={emailData.message}
                  onChange={(e) => setEmailData({...emailData, message: e.target.value})}
                  required
                  placeholder="Type your message here..."
                  rows="6"
                />
              </div>
              <div className="modal-actions">
                <button type="button" onClick={() => setShowEmailForm(false)} className="cancel-btn">
                  Cancel
                </button>
                <button type="submit" className="submit-btn">
                  Send Email
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default FacultyDashboard;
