import React from "react";
import "./ViewStudents.css";

const ViewStudents = () => {
  const students = [
    { id: 1, name: "John Doe", rollNo: "20CS101", branch: "CSE", year: 3, cgpa: 8.5, attendance: "85%" },
    { id: 2, name: "Jane Smith", rollNo: "20CS102", branch: "CSE", year: 3, cgpa: 9.0, attendance: "90%" },
    // Add more sample data as needed
  ];

  return (
    <div className="view-students">
      <h2>Student List</h2>
      <div className="filters">
        <input type="text" placeholder="Search by name or roll number..." />
        <select>
          <option value="">All Branches</option>
          <option value="CSE">CSE</option>
          <option value="ECE">ECE</option>
        </select>
        <select>
          <option value="">All Years</option>
          <option value="1">1st Year</option>
          <option value="2">2nd Year</option>
        </select>
      </div>
      <table className="students-table">
        <thead>
          <tr>
            <th>Roll No</th>
            <th>Name</th>
            <th>Branch</th>
            <th>Year</th>
            <th>CGPA</th>
            <th>Attendance</th>
          </tr>
        </thead>
        <tbody>
          {students.map(student => (
            <tr key={student.id}>
              <td>{student.rollNo}</td>
              <td>{student.name}</td>
              <td>{student.branch}</td>
              <td>{student.year}</td>
              <td>{student.cgpa}</td>
              <td>{student.attendance}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ViewStudents;
