import React, { useState } from 'react';
import './ViewStudentsHOD.css';

const ViewStudentsHOD = () => {
    const [searchTerm, setSearchTerm] = useState('');
    const [filterBranch, setFilterBranch] = useState('all');
    const [filterYear, setFilterYear] = useState('all');
    const [selectedStudent, setSelectedStudent] = useState(null);
    const [showAssignModal, setShowAssignModal] = useState(false);

    // Handle view details
    const handleViewDetails = (student) => {
        setSelectedStudent(selectedStudent?.id === student.id ? null : student);
    };

    // Handle assign mentor
    const handleAssignMentor = (student) => {
        setSelectedStudent(student);
        setShowAssignModal(true);
    };

    return (
        <div className="view-students-hod">
            {/* ...existing filters code... */}

            <table className="students-table">
                {/* ...existing table header... */}
                <tbody>
                    {filteredStudents.map(student => (
                        <>
                            <tr key={student.id}>
                                <td>{student.rollNo}</td>
                                <td>{student.name}</td>
                                <td>{student.branch}</td>
                                <td>{student.year}</td>
                                <td>{student.cgpa}</td>
                                <td>{student.mentor}</td>
                                <td>
                                    <button 
                                        className="view-btn"
                                        onClick={() => handleViewDetails(student)}
                                    >
                                        {selectedStudent?.id === student.id ? 'Hide Details' : 'View Details'}
                                    </button>
                                    <button 
                                        className="assign-btn"
                                        onClick={() => handleAssignMentor(student)}
                                    >
                                        Assign Mentor
                                    </button>
                                </td>
                            </tr>
                            {selectedStudent?.id === student.id && (
                                <tr className="details-row">
                                    <td colSpan="7">
                                        <div className="student-details">
                                            <h3>Student Details</h3>
                                            <p><strong>Email:</strong> {student.email}</p>
                                            <p><strong>Phone:</strong> {student.phone}</p>
                                            <p><strong>Address:</strong> {student.address}</p>
                                            <p><strong>Current Mentor:</strong> {student.mentor}</p>
                                            <p><strong>Academic Performance:</strong></p>
                                            <ul>
                                                <li>CGPA: {student.cgpa}</li>
                                                <li>Attendance: {student.attendance}%</li>
                                                <li>Backlogs: {student.backlogs || 0}</li>
                                            </ul>
                                        </div>
                                    </td>
                                </tr>
                            )}
                        </>
                    ))}
                </tbody>
            </table>

            {/* Mentor Assignment Modal */}
            {showAssignModal && (
                <div className="modal">
                    <div className="modal-content">
                        <h3>Assign Mentor to {selectedStudent.name}</h3>
                        <select>
                            <option value="">Select Mentor</option>
                            <option value="1">Dr. Smith</option>
                            <option value="2">Dr. Johnson</option>
                            <option value="3">Dr. Brown</option>
                        </select>
                        <div className="modal-actions">
                            <button onClick={() => setShowAssignModal(false)}>Cancel</button>
                            <button className="assign-btn">Confirm Assignment</button>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default ViewStudentsHOD;
