import React, { useState } from 'react';
import './ViewFaculty.css';

const ViewFaculty = () => {
    const [searchTerm, setSearchTerm] = useState('');
    const [departmentFilter, setDepartmentFilter] = useState('all');

    const faculty = [
        { id: 1, name: "Dr. Sarah Johnson", department: "CSE", designation: "Professor", email: "sarah@college.edu" },
        { id: 2, name: "Dr. Michael Brown", department: "ECE", designation: "Associate Professor", email: "michael@college.edu" }
    ];

    return (
        <div className="view-faculty-container">
            <h2>Faculty Management</h2>

            <div className="controls">
                <input 
                    type="text" 
                    placeholder="Search faculty..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                />
                <select 
                    value={departmentFilter}
                    onChange={(e) => setDepartmentFilter(e.target.value)}
                >
                    <option value="all">All Departments</option>
                    <option value="CSE">CSE</option>
                    <option value="ECE">ECE</option>
                    <option value="MECH">MECH</option>
                </select>
            </div>

            <div className="faculty-grid">
                {faculty.map(member => (
                    <div key={member.id} className="faculty-card">
                        <h3>{member.name}</h3>
                        <p><strong>Department:</strong> {member.department}</p>
                        <p><strong>Designation:</strong> {member.designation}</p>
                        <p><strong>Email:</strong> {member.email}</p>
                        <div className="faculty-actions">
                            <button className="edit-btn">Edit</button>
                            <button className="view-btn">View Details</button>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default ViewFaculty;
